        static double[] FreqRUS(string s)
        {
            var resultRUS = new double['Я' - 'А' + 1];
            foreach (var c in s.ToUpper())
                if ((c >= 'А') && (c <= 'Я')) resultRUS[c - 'А']++;
            for (int i = 0; i < resultRUS.Length; i++)
                resultRUS[i] /= s.Length;
            return resultRUS;
        }

        static double[] FreqENG(string s)
        {
            var resultENG = new double['Z' - 'A' + 1];
            foreach (var c in s.ToUpper())
                if ((c >= 'A') && (c <= 'Z')) resultENG[c - 'A']++;
            for (int i = 0; i < resultENG.Length; i++)
                resultENG[i] /= s.Length;
            return resultENG;
